INSERT INTO ForumEntries VALUES('VI0001', 2016, 1, 'P0000009I', '2016-07-19 06:36:38', 'A0000018D', '2017-06-09 10:13:58', 'We would highly recommend Fred to anyone purchasing or selling a home. It was a privilege to work with him, Shannan, and the rest of the North&Co team!');
