INSERT INTO ForumEntries VALUES('VI0001', 2018, 2, 'P0000003N', '2016-12-06 03:22:08', 'A0000024F', '2016-04-21 17:58:52', 'Had the occasion over the last few weeks to experience their sports medicine/physical therapy department.  Outstanding.  Drs. Anthony "Tony" Walker and Jennifer Hannon are a great team.  They really know their stuff.');
