INSERT INTO ForumEntries VALUES('VI0001', 2018, 2, 'P0000003N', '2016-12-06 03:22:08', 'A0000014A', '2016-05-10 11:44:49', 'I adore Ikea! There is nothing better to me than seeing a piece in it"s functionality and how it will work in a room, hence, the showroom.');
