INSERT INTO ForumEntries VALUES('VI0001', 2018, 2, 'P0000003N', '2016-12-06 03:22:08', 'A0000015E', '2016-06-26 01:30:55', 'Food was pretty good however the prices are high. Waitress did a great job until she asked if I needed change.');
INSERT INTO ForumEntries VALUES('VI0001', 2018, 2, 'P0000003N', '2016-12-06 03:22:08', 'A0000002K', '2016-04-19 12:36:11', 'Back to the Eggs Benedict. Everything on the Benny was cheap. The English muffin WASN"T TOASTED! Never seen that before. The hash browns were WAY too crispy.. Like eating unflavored Pop Rocks.. The Hollandaise sauce was just ok..');
