INSERT INTO ForumEntries VALUES('NZ0003', 2016, 2, 'P0000008O', '2019-04-25 22:22:50', 'A0000024F', '2019-11-06 15:30:44', 'Overall prompt friendly service, quaint little patio, average burger but good for a quick fix in satisfying the burger craving.');
INSERT INTO ForumEntries VALUES('NZ0003', 2016, 2, 'P0000008O', '2019-04-25 22:22:50', 'A0000007U', '2016-10-01 05:53:53', 'The wait staff is always nice and attentive. I remember the first time we visited, it was pretty busy but we didn"t have to wait too long for a table.');
