INSERT INTO ForumEntries VALUES('NZ0003', 2017, 1, 'P0000009I', '2019-07-12 21:03:55', 'A0000006W', '2018-06-27 13:05:52', 'Met an old close friend for dinner at Portofino.  Great intimate dinning with class. Excellent pasta and meat dishes. Delightful service and great drinks. Will come back!');
