INSERT INTO ForumEntries VALUES('NZ0003', 2017, 1, 'P0000009I', '2017-11-18 04:02:14', 'A0000004V', '2017-07-31 06:30:52', 'Drive-thru style smog station conveniently located close to the DMV.  I had a bit of trouble finding it via google maps- it"s behind the 7-11 across the street from Smith"s. Super quick service, I was done in about 5 minutes. 10/10 would recommend.');
