INSERT INTO ForumEntries VALUES('FH0004', 2018, 1, 'P0000008O', '2017-06-19 17:20:07', 'A0000006W', '2017-05-13 04:52:46', 'Im sure I left something out but you get the gist right? If not....stay here!! You won"t regret it!');
