INSERT INTO ForumEntries VALUES('GO0005', 2018, 1, 'P0000007E', '2018-05-25 23:48:06', 'A0000008Z', '2016-06-07 12:11:46', 'This is one of the best "hole in the wall" spots I"ve been to in years! Fresh home-made tortillas, great steak, people are amazing! I came with a party of 10 ppl and the food was fast, delicious and prefer was perfect. I will definitely be back!');
INSERT INTO ForumEntries VALUES('GO0005', 2018, 1, 'P0000007E', '2018-05-25 23:48:06', 'A0000023E', '2019-06-30 19:36:54', 'I had the LA wrap and my wife had more soup. Both were really good. They make fresh, homemade soup every day.');
