INSERT INTO ForumEntries VALUES('JX0006', 2016, 1, 'P0000006T', '2017-01-24 18:10:31', 'A0000001Q', '2018-07-01 21:09:05', 'The coffee I had was pretty gross. The cappuccino was barely foamy, and the coffee was kind of rancidly bitter.');
INSERT INTO ForumEntries VALUES('JX0006', 2016, 1, 'P0000006T', '2017-01-24 18:10:31', 'A0000001Q', '2017-09-04 08:37:16', 'Gorgeous hotel! I felt like a VIP. Definitely the next best thing to the real Venice, in my opinion. Lots of pools to choose from and great casino. Convenient location- close to everything. Would definitely go there again.');
INSERT INTO ForumEntries VALUES('JX0006', 2016, 1, 'P0000006T', '2017-01-24 18:10:31', 'A0000021Y', '2019-05-22 09:31:24', 'The young man grating the cheese played with his smartphone typing out a few messages. Replaced it to his pocket and continued handling the cheese without washing his hands or using any plastic gloves.');
INSERT INTO ForumEntries VALUES('JX0006', 2016, 1, 'P0000006T', '2018-01-10 02:48:46', 'A0000010W', '2017-03-29 15:30:50', 'Not a fan. Overpriced, over cooked bland burgers. Everything is extra (fries, onion rings).');
