INSERT INTO ForumEntries VALUES('JX0006', 2019, 1, 'P0000005Y', '2018-02-25 15:52:31', 'A0000026E', '2019-11-03 11:24:08', 'Wiki Wiki is like the middle ground from your typical gas station auto wash and full service washes with the personal touch.');
