INSERT INTO ForumEntries VALUES('JX0006', 2019, 1, 'P0000005Y', '2016-12-30 06:56:33', 'A0000008Z', '2018-11-01 14:31:25', '2015 St. Patrick"s Day Festival highlights: Saturday morning parking was available for free at 1) Parking Garage near City Hall, located at Basic Road and Lead Street. Accessed from Basic Road and 2) Parking Garage near Justice Facility, located at Water Street, near Basic Road. Accessed from either Water Street or Texas Avenue.');
