INSERT INTO ForumEntries VALUES('BD0007', 2016, 2, 'P0000004D', '2017-07-22 00:40:01', 'A0000005P', '2018-07-31 07:28:48', 'We had dinner here and the food was excellent and the service couldn"t have been better!');
