INSERT INTO ForumEntries VALUES('BD0007', 2019, 2, 'P0000006T', '2017-05-17 06:05:22', 'A0000005P', '2016-05-14 23:08:46', 'As I recall I traded a few emails and phone calls with Chip and he was helpful in answering all my questions. We met at 626 North Graham and he showed me the unit there that was available.');
