INSERT INTO ForumEntries VALUES('BD0007', 2019, 2, 'P0000006T', '2017-05-17 06:05:22', 'A0000005P', '2017-11-01 03:15:06', 'Took my kids here to hang out on one fine 72 degree sunny Saturday in February. Going over the bridge, coming from Aliante Casino, you cant miss the nice view of the waterfall that is at the forefront of the park.');
