INSERT INTO ForumEntries VALUES('PA0008', 2019, 2, 'P0000003N', '2019-07-16 17:37:22', 'A0000016U', '2017-07-25 10:42:56', 'Once you are done exploring the inside exhibits and views, you can wander outside and walk the dam. Beware that when you leave the dam touring area, you cannot return, it is a one way only exit.');
