INSERT INTO ForumEntries VALUES('IX0001', 2019, 2, 'P0000007I', '2019-07-18 23:45:45', 'A0000293W', '2019-06-22 23:19:00', 'Very disappointing.  Overpriced mediocre food.  Refried beans served with fajitas were so hot that they not only burned my tongue but throat as well.');
