INSERT INTO ForumEntries VALUES('IB0002', 2019, 2, 'P0000008J', '2018-10-18 09:12:25', 'A0000222H', '2019-02-19 09:21:10', 'He took his time with my hair to bring it to orgasmic perfection as he explained.');
