INSERT INTO ForumEntries VALUES('IB0002', 2019, 2, 'P0000008J', '2018-10-18 09:12:25', 'A0000222H', '2019-04-07 18:01:12', 'Fabulous. A friend took me here last night. Wonderful food, we had panang curry, duck fried rice and Chilean sea bass.  What a wonderful combination of flavors.');
