INSERT INTO ForumEntries VALUES('CS2102', 2018, 1, 'P0000008J', '2019-06-11 16:11:48', 'A0000270F', '2019-08-29 09:12:18', 'We finished eating and paid for the bill at 7:50p.m. I turned around and noticed FOUR empty 2 person tables. They had never been sat, but we had to sit with an unfamiliar couple!');
