INSERT INTO ForumEntries VALUES('DE0008', 2018, 2, 'P0000006U', '2018-03-07 07:04:26', 'A0000104L', '2018-06-17 12:50:39', 'While I enjoyed the company of my friends, I would not come back to this restaurant, primarily because of the poor service. Once we were all seated, we were not greeted by anyone for over half an hour, and when someone did come by, we asked whether we could place our orders, to which he said he could not; he"s doing something else - so we waited at least another 15 minutes before someone came to finally take our orders.');
INSERT INTO ForumEntries VALUES('DE0008', 2018, 2, 'P0000006U', '2018-03-07 07:04:26', 'A0000192N', '2019-06-27 22:57:55', 'Snow and Kind crab, caviar, steak tartare, all types of meat and cuts, sushi, soups, Asian cuisine, pastas and pizzas. Literally all you can think about.');
