INSERT INTO ForumEntries VALUES('CS3103', 2018, 1, 'P0000003P', '2019-10-09 08:32:27', 'A0000051V', '2018-06-15 01:26:35', 'I"ve received great service and highly recommend Stone Law. I"ve been a client for many years and can attest they do great work!');
