INSERT INTO ForumEntries VALUES('CS3103', 2018, 1, 'P0000003P', '2018-12-14 06:53:38', 'A0000153P', '2018-12-22 08:08:48', 'Will I come back...  yes.  But will not go out of way to come here.  I don"t usually have cravings for curry (never did).');
